import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import '../CSS folder/style.css'


export default function Dashboard() {

  let navigateTo = useNavigate();

  const { email } = useParams();

  const [student, setStudent] = useState([]);

  const getStudentDetails = async (email) => {

    // e.preventDefault();
    // await axios.get(`http://localhost:4000/getStudentByEmail/${email}`)
    //   .then((result) => {
    //     console.log("fetched student details", result.data);
    //     setStudent(result.data);

    //     localStorage.getItem("studentData", student.email);
    //   });

    await axios.get('http://localhost:4000/getAllStudents')
      .then((result) => {
        console.log("fetched student details", result.data);
        setStudent(result.data);

        localStorage.getItem("studentData", student.email);
      });
  }

  useEffect(() => {
    getStudentDetails();
  }, []);

  const handleLogout = () => {
    // e.preventDefault();
    setStudent({});

    if (localStorage.getItem("studentData")) {
      localStorage.removeItem("studentData");
      localStorage.clear();
      navigateTo('/');
    }
    else {
      navigateTo('/dashboard');
      localStorage.clear();
    }

    // let session = localStorage.getItem("studentData");
    // if (session == null) {
    //   localStorage.removeItem("remove");
    //   navigateTo('/');
    // }
    // sessionStorage.setItem("studentData", 1);
  };
  // window.addEventListener("load", handleLogout);

  return (
    <>
      <div className="wrapper">
        <div className="inner">
          <form>
            <h3> !!Welcome Student!! {student.email} </h3>
            <p>
              <b>This is Landing page for Student</b><br />
              <b>Students need to login to get here</b>
            </p>

            <div>
              <center className='land'>
                {student.map((stud, index) => (

                  <tr>
                    <th scope="row" key={index}>{index + 1}</th>
                    {/* <th scope="row" >{stud.id}</th> */}
                    <td>{stud.name}</td>
                    <td>{stud.username}</td>
                    <td>{stud.email}</td>
                  </tr>
                ))}
              </center>
            </div>
            <button onClick={handleLogout}>Logout</button>
          </form>
        </div>
      </div>
    </>

  )
}
