import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import '../CSS folder/style.css'


export default function Register() {

	let navigateTo = useNavigate();

	const navTo = useNavigate();
	const gotToLogin = () => navTo('/login');

	const [student, setStudent] = useState({
		name: "",
		email: "",
		password: ""
	})

	// const { name, email, password } = student;

	const handleChange = (e) => {

		const { name, value } = e.target
		setStudent({
			...student,
			[name]: value
		})
	}

	const onSubmit = async (e) => {

		const { name, email, password } = student
		e.preventDefault();

		if (name && email && password) {
			await axios.post('http://localhost:4000/register', student)
				.then((result) => {
					if (result) {
						alert('Student registered successfully. Login to proceed further');
						console.log("Student registered successfully. Login to proceed further");
						navigateTo("/login");
					}
				})
		} else {
			console.log("Please Fill all details to register");
			alert('Please Fill all details to register');
		}

	}

	useEffect(() => {
		onSubmit();
	}, []);


	return (
		<>
			{/* {console.log( "Student object",student)} */}
			<div className="wrapper">
				<div className="inner">
					<form onSubmit={(e) => onSubmit(e)} >
						<h3>Registration</h3>
						<p>Register/Add New Student</p>
						<p>FullStack Student Registration Web-App using MERN</p>

						<label className="form-group">
							<input type="text" name='name' value={student.name} className="form-control" onChange={handleChange}
								onInvalid="this.setCustomValidity('name is required..')" onInput={this.setCustomValidity('')} />
							<span>Enter Name</span>
							<span className="border"></span>
						</label>

						<label className="form-group">
							<input type="email" name='email' value={student.email} className="form-control" onChange={handleChange} />
							<span htmlFor="">Enter Email-id</span>
							<span className="border"></span>
						</label>

						<label className="form-group" >
							<input type="password" name='password' value={student.password} className="form-control" onChange={handleChange} />
							<span htmlFor="">Enter Password</span>
							<span className="border"></span>
						</label>

						<button >
							Sign Up
							<i className="zmdi zmdi-arrow-right"></i>
						</button>
						<button onClick={gotToLogin} >
							Login
							<i className="zmdi zmdi-arrow-right"></i>
						</button>
					</form>
				</div>
			</div>
		</>
	)
}
