import React, { useEffect, useState } from 'react'
import '../Register/Register.css'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'


export default function Login () {

    let navigateTo = useNavigate();

    const goToRegister = () => navigateTo('/registration')

    const [student, setStudent] = useState({
        
        email : "",
        password : ""
    })


    const { email, password } = student;

	const handleChange = (e) => {

		const { name, value } = e.target
		setStudent({
			...student,
			[name] : value
		})
	}


    const handleLogin = async (e) => {
        
        // e.preventDefault();
        if (email && password) {
            await axios.post('http://localhost:4000/login', student)
            .then((result) => {
                console.log(result);
            })
            // alert("login successful");
            navigateTo("/dashboard");
        } else {
            alert("Enter login details for proceeding further");
        }

    }

    useEffect(() => {
        handleLogin();
    }, []);


  return (
    <>
        <div className="wrapper">
			<div className="inner">
				<form action="">
					<h3>Registration</h3>
                    <p>login students here</p>
					<label className="form-group">
						<input type="email" name='email' value={student.email} className="form-control" onChange={ handleChange } required />
						<span htmlFor="">Enter Email-id</span>
						<span className="border"></span>
					</label>

					<label className="form-group" >
                        <input type="password" name='password' value={student.password} className="form-control" onChange={ handleChange } required />
						<span htmlFor="">Enter Password</span>
						<span className="border"></span>
					</label>

					<button onClick={ handleLogin }>
                        Login 
						<i className="zmdi zmdi-arrow-right"></i>
					</button>

                    <button onClick={ goToRegister }>
                        Sign Up 
						<i className="zmdi zmdi-arrow-right"></i>
					</button>
				</form>
			</div>
		</div>
    </>
  )
}
