import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import '../CSS folder/style.css'


export default function Dashboard() {

  let navigateTo = useNavigate();

  const { email } = useParams();

  const [student, setStudent] = useState([]);

  const getStudentDetails = async (email) => {

    // e.preventDefault();
    // await axios.get(`http://localhost:4000/getStudentByEmail/${email}`)
    //   .then((result) => {
    //     console.log("fetched student details", result.data);
    //     setStudent(result.data);

    //     localStorage.getItem("studentData", student.email);

    //     sessionStorage.getItem("studentData", student.email);
    //   });

    await axios.get('http://localhost:4000/getAllStudents')
      .then((result) => {
        console.log("fetched student details", result.data);
        setStudent(result.data);

        // localStorage.getItem("studentData", student.email);

        sessionStorage.getItem("studentData");
      });
  }

  useEffect(() => {
    getStudentDetails();
  }, []);

  const handleLogout = () => {
    // e.preventDefault();
    // setStudent({});

    // if (localStorage.getItem("studentData")) {
    //   localStorage.clear();
    //   navigateTo('/');
    //   alert('student logged out');
    // }
    // else {
    //   navigateTo('/');
    // }


    // session storage manage
    if (sessionStorage.getItem("studentData")) {
      sessionStorage.clear();
      navigateTo('/');
      alert('Student logged out');
      // alert(`${sessionStorage.getItem("studentData", student.email)}` + ' logged out');
    }
    else {
      navigateTo('/');
    }

  };



  return (
    <>
      <div className="wrapper">
        <div className="inner">
          <form>
            <h3> !!Welcome Student!! {sessionStorage.getItem("studentData")} </h3>
            <p>
              <b>This is Landing page for Student</b><br />
              <b>Students need to login to get here</b>
            </p>

            <div align='center' className=''>

              {student.map((stud, index) => (

                <tr>
                  <th scope="row" key={index}>{index + 1}</th>
                  <td>{stud.name}</td>
                  <td>{stud.email}</td>
                </tr>

              ))}
            </div>
            <button onClick={handleLogout}>Logout</button>
          </form>
        </div>
      </div>
    </>

  )
}
