import React, { useEffect, useState } from 'react'
import './Register.css'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'


export default function Register () {

	let navigateTo = useNavigate();

	const navTo = useNavigate();
	const gotToLogin = () => navTo('/login');

    const [student, setStudent] = useState({
        name : "",
        email : "",
        password : ""
    })

    const { name, email, password } = student;

	const handleChange = (e) => {

		const { name, value } = e.target
		setStudent({
			...student,
			[name] : value
		})
	}

	const handleRegister = async (e) => {

		const {name, email, password} = student
		e.preventDefault();
		if(name && email && password){
			await axios.post('http://localhost:4000/register', student)
			.then((result) => { 
				console.log(result);
			})
			alert("Registered");
			navigateTo("/login");
		} else {
			alert("Enter all required details")
		}

		
			
	}

	useEffect(() => {
		handleRegister();
	}, []);


  return (
    <>
		{/* {console.log( "Student object",student)} */}
        <div className="wrapper">
			<div className="inner">
				<form action="">
					<h3>Registration</h3>
                    <p>Register/Add New Student</p>

					<label className="form-group">
						<input type="text" name='name' value={student.name} className="form-control" onChange={ handleChange }  required />
						<span>Enter Name</span>
						<span className="border"></span>
					</label>

					<label className="form-group">
						<input type="email" name='email' value={student.email} className="form-control" onChange={ handleChange }  required />
						<span htmlFor="">Enter Email-id</span>
						<span className="border"></span>
					</label>

					<label className="form-group" >
                        <input type="password" name='password' value={student.password} className="form-control" onChange={ handleChange } required />
						<span htmlFor="">Enter Password</span>
						<span className="border"></span>
					</label>

					<button onClick={ handleRegister }>
                        Sign Up 
						<i className="zmdi zmdi-arrow-right"></i>
					</button>
					<button onClick={gotToLogin} >
                        Login 
						<i className="zmdi zmdi-arrow-right"></i>
					</button>
				</form>
			</div>
		</div>
    </>
  )
}
